import React from 'react';
import chef1 from './chef1.jpg';
import chef2 from './chef2.jpg';
import chef3 from './chef3.jpg';
import chef4 from './chef4.jpg';
import chef5 from './chef5.jpg';
import Slider from 'react-slick';

export class Chef extends React.Component {
    render() {
        const settings = {
            dots: true,
            centerMode: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
          };

        return(
            <div className="container">
                <h2 style={{color: '#2ecc71', textAlign: 'center'}}>Our Delicious</h2>
                <h2 style={{textAlign: 'center'}}>Meet Our Expert Chefs</h2>
                <Slider>
                    <div>
                        <img className="center chef_spec" src={chef1} alt="Chef1" />
                    </div>
                    <div>
                        <img className="center chef_spec" src={chef2} alt="Chef2" />
                    </div>
                    <div>
                        <img className="center chef_spec" src={chef3} alt="Chef3" />
                    </div>
                    <div>
                        <img className="center chef_spec" src={chef4} alt="Chef4" />
                    </div>
                    <div>
                        <img className="center chef_spec" src={chef5} alt="Chef5" />
                    </div>
                </Slider>
            </div>
        );
    }
}
