import React from 'react';
import $ from 'jquery';
import slick1 from './slick_slider-1.jpeg';
import slick2 from './slick_slider-2.jpeg';
import slick3 from './slick_slider-3.jpeg';
import slick4 from './slick_slider-4.jpeg';

export class Menu extends React.Component {

    componentDidMount = () => {
        $('.center').slick({
            centerMode: true,
            infinite: true,
            centerPadding: '60px',
            slidesToShow: 3,
            speed: 500,
            variableWidth: false
        });
        $('.center').on('beforeChange', function (event, slick, currentSlide, nextSlide) {
            console.log('beforeChange', currentSlide, nextSlide);
        });
        $('.center').on('afterChange', function (event, slick, currentSlide) {
            console.log('afterChange', currentSlide);
        });
        
    }

    render() {
        return(
            <div className="container container1">
                <h2>Our Special Dishes</h2>
                <h2>Lets Discover Our Food</h2>
                
                {/* <div className="card" style={{width: '18rem'}}>
                    <img className="card-img-top" src={slick1} />   
                    <div className="card-body">
                        <h3 className="card-text" style={{float: 'left'}}>Menu</h3>
                        <h3 className="card-text" style={{float: 'right'}}>$20</h3> 
                        <p class="card-text" style={{float:'left'}}>Lorem Ipsum</p>
                    </div>
                </div> */}

                <div class="slider center">
    <div class="clip"><h3><div class="top">top</div><div>1</div><div class="bottom">btm</div></h3></div>
    <div class="clip"><h3><div class="top">top</div><div>2</div><div class="bottom">btm</div></h3></div>
    <div class="clip"><h3><div class="top">top</div><div>3</div><div class="bottom">btm</div></h3></div>
    <div class="clip"><h3><div class="top">top</div><div>4</div><div class="bottom">btm</div></h3></div>
    <div class="clip"><h3><div class="top">top</div><div>5</div><div class="bottom">btm</div></h3></div>
    <div class="clip"><h3><div class="top">top</div><div>6</div><div class="bottom">btm</div></h3></div>
  </div>




            </div>
        );
    }
}
